// 获取所有图标名称
const iconNames = Object.keys(iconMap)

/**
 * 根据字符串生成稳定的哈希值
 */
function hashString(str: string): number {
  let hash = 0
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i)
    hash = ((hash << 5) - hash) + char
    hash = hash & hash // Convert to 32bit integer
  }
  return Math.abs(hash)
}

/**
 * 获取图标名称
 */
function getIconName(): string {
  // 如果指定了name，直接使用
  if (props.name && iconMap[props.name]) {
    return props.name
  }
  
  // 如果指定了seed，基于seed生成稳定的图标
  if (props.seed) {
    const hash = hashString(props.seed)
    const index = hash % iconNames.length
    return iconNames[index]
  }
  
  // 如果name指定了但不存在，随机返回一个
  if (props.name && !iconMap[props.name]) {
    console.warn(`图标 "${props.name}" 不存在，将随机返回一个图标`)
  }
  
  // 完全随机
  const randomIndex = Math.floor(Math.random() * iconNames.length)
  return iconNames[randomIndex]
}

// 当前显示的图标SVG
const currentIconSvg = computed(() => {
  const iconName = getIconName()
  return iconMap[iconName] || ''
})

// 自定义class
const customClass = computed(() => props.class)
</script>

<script lang="ts">
// 导出图标相关的工具函数
export const emojiIconNames = ["-angel", "-astonished", "-cool", "-cry", "-dizzy", "-expressionless", "-happy-", "-happy-_1", "-in-love", "-injury", "-kiss-", "-mute", "-sad", "-scared-", "-scared", "-smile-", "-smiling", "-thinking", "-tired", "-unamused", "-wink"]

export function getAllEmojiIconNames(): string[] {
  return [...emojiIconNames]
}

export function getEmojiIconCount(): number {
  return emojiIconNames.length
}

export function hasEmojiIcon(name: string): boolean {
  return emojiIconNames.includes(name)
}
</script>

